def network_conf():
    router_ip = '192.168.8.1'  # Isi dengan IP modem
    username = 'admin'  # Isi username login modem
    password = 'admin'  # Isi password login modem
    mode = '4g'  # Isi sesuai kebutuhan anda sesuai dengan panduan
    lteband = '1'  # Isi sesuai kebutuhan anda sesuai dengan panduan
    secondary_lteband = '0'  # Isi sesuai kebutuhan anda sesuai dengan panduan (isi 0 jika tidak diperlukan)
    primary_lteband = '0'  # Isi sesuai kebutuhan anda sesuai dengan panduan (isi 0 jika tidak diperlukan)
    
    return router_ip, username, password, mode, lteband, secondary_lteband, primary_lteband

# > PANDUAN DAFTAR MODE YANG BISA DIPAKAI:
# - 'auto': Untuk otomatis
# - '2g': Untuk jaringan 2G Only
# - '3g': Untuk jaringan 3G Only
# - '3g2g': Untuk jaringan 2G dan 3G Auto
# - '4g': Untuk jaringan 4G Only
# - '4g2g': Untuk jaringan 4G dan 2G Auto
# - '4g3g': Untuk jaringan 4G dan 3G auto

# > PANDUAN DAFTAR LTEBAND YANG BISA DIPAKAI:
# - 'all': Menggunakan semua band
# - '1': Untuk B1
# - '3': Untuk B3
# - '7': Untuk B7
# - '8': Untuk B8
# - '20': Untuk B20
# - '28': Untuk B28
# - '38': Untuk B38
# - '40': Untuk B40

